INSERT INTO `articles` (`id`, `category`, `title`, `description`, `slug`, `status`, `photo`, `views`, `last_update`, `date`) VALUES
(1, 1, '👋 الترحيب', '<h3>أهلًا ومرحبًا بكم في \"وثيقة\"</h3><p><br></p><h4>وثيقة هو سكربت متكامل لإنشاء وتوثيق المشاريع</h4><p>\"وثيقة\" هو سكربت مبتكر يتيح لك إنشاء وتنسيق وثائق المشاريع بسهولة ومرونة، شبيه بمنصة GitBook. </p><p>يوفر \"وثيقة\" واجهة بسيطة وأنيقة تساعدك على تنظيم المحتوى في فصول وصفحات مترابطة، مما يجعل إدارة وتصفح المستندات سلسًا للمطورين والفرق التقنية.</p><p><br></p><h5><b>مميزات السكربت:</b></h5><li data-start=\"378\" data-end=\"449\">📝 <strong data-start=\"383\" data-end=\"403\">محرر نصوص متطور:</strong> دعم محرر النصوص <a href=\"https://summernote.org/\" target=\"_blank\">Summernote</a> لتنسيق النصوص بسرعة.</li><li data-start=\"450\" data-end=\"549\">📂 <strong data-start=\"455\" data-end=\"473\">تنظيم المحتوى:</strong> إنشاء كتب أو مستندات مقسمة إلى أبواب وفصول مع قائمة جانبية للتنقل السريع.</li><li data-start=\"550\" data-end=\"618\">🔍 <strong data-start=\"555\" data-end=\"574\">بحث داخلي فوري:</strong> محرك بحث متكامل للعثور على المحتوى بسرعة.</li><li data-start=\"619\" data-end=\"696\">🌐 <strong data-start=\"624\" data-end=\"641\">متعدد اللغات:</strong> دعم اللغات المتعددة، بما في ذلك العربية والإنجليزية.</li><li data-start=\"697\" data-end=\"776\">🔒 <strong data-start=\"702\" data-end=\"722\">آمن ومحمي:</strong> تم استخدام أحدث الطرق التقنية لحماية السكربت من أشهر الثغرات الأمنية.</li><li data-start=\"883\" data-end=\"959\">🎨 <strong data-start=\"888\" data-end=\"906\">متعدد القوالب:</strong> يدعم تعدد القوالب، بحيث يمكن تخصيص القالب بحسب ما يناسب احتياجاتكم، القوالب مبرمجة بتقنية Twig.</li><p><br></p><h5><b>الاستخدامات:</b></h5><ul data-start=\"984\" data-end=\"1142\"><li data-start=\"984\" data-end=\"1016\">🏗️ توثيق المشاريع البرمجية.</li><li data-start=\"1017\" data-end=\"1047\">📘 إعداد الأدلة التعليمية.</li><li data-start=\"1048\" data-end=\"1088\">📑 كتابة كتيبات الإرشادات والسياسات.</li><li data-start=\"1089\" data-end=\"1142\">🧠 مشاركة المعرفة الداخلية داخل الفرق أو الشركات.</li></ul><p data-start=\"1144\" data-end=\"1255\">سواء كنت مطورًا فرديًا أو جزءًا من فريق عمل، فإن \"وثيقة\" هو الحل الأمثل لتوثيق مشاريعك بشكل احترافي ومرتب! 🚀</p><p data-start=\"1144\" data-end=\"1255\"><br></p><h5><b>الترخيص:</b></h5><ul><li>🟢 يسمح بتعديل ونشر هذا السكربت في أي مكان ومن قبل أي جهة</li><li>🛑 يمنع بيع هذا السكربت بدون التعديل عليه.</li><li>🟢 يسمح ببيع قوالب مخصصة للسكربت</li></ul><p data-start=\"1144\" data-end=\"1255\"><br></p><h5><b>الحقوق:</b></h5><p data-start=\"1144\" data-end=\"1255\">تمت برمجة هذا السكربت بواسطة</p><ul><li><a href=\"https://akour.me/ar/\" target=\"_blank\">المبرمج محمد عكور</a></li><li><a href=\"https://abdelhak.me\" target=\"_blank\">المبرمج بوجليدة عبد الحق</a>&nbsp;</li></ul><p><br></p><h5>المراجع:</h5><p>صورة شعار سكربت \"وثيقة\" مرخصة من&nbsp;<a href=\"https://www.freeimages.com/\" target=\"_blank\">Freeimages.com</a></p><p></p>', 'get-started', 'enable', NULL, 20, '2025-02-27 13:02:42', '2025-02-14 02:35:17'),
(4, 1, '📋 متطلبات التشغيل', '<h3><span data-start=\"52\" data-end=\"71\">متطلبات التشغيل</span></h3><p data-start=\"75\" data-end=\"162\">لتشغيل سكربت <strong data-start=\"88\" data-end=\"99\">\"وثيقة\"</strong> بسلاسة وأداء عالٍ، تأكد من توفر المتطلبات التالية على خادمك:</p><ul data-start=\"164\" data-end=\"670\"><li data-start=\"164\" data-end=\"198\">🟢 <strong data-start=\"169\" data-end=\"179\">اللغة:</strong> PHP 8.2 أو أعلى.</li><li data-start=\"199\" data-end=\"251\">🌍 <strong data-start=\"204\" data-end=\"219\">خادم الويب:</strong> Apache أو Nginx (حسب تفضيلك).</li><li data-start=\"252\" data-end=\"318\">🗃️ <strong data-start=\"258\" data-end=\"277\">قاعدة البيانات:</strong> MySQL أو MariaDB (اختياري حسب الحاجة).</li><li data-start=\"319\" data-end=\"409\">📦 <strong data-start=\"324\" data-end=\"340\">إدارة الحزم:</strong> Composer (اختياري، لكنه يوصى به لتسهيل إدارة المكتبات والتحديثات).</li><li data-start=\"410\" data-end=\"461\">🛡️ <strong data-start=\"416\" data-end=\"424\">SSL:</strong> شهادة SSL لتأمين الاتصال (مستحسن).</li><li data-start=\"462\" data-end=\"596\">🔧 <strong data-start=\"467\" data-end=\"491\">الامتدادات المطلوبة:</strong><ul data-start=\"496\" data-end=\"596\"><li data-start=\"496\" data-end=\"510\"><code data-start=\"498\" data-end=\"508\">mbstring</code></li><li data-start=\"513\" data-end=\"523\"><code data-start=\"515\" data-end=\"521\">json</code></li><li data-start=\"526\" data-end=\"535\"><code data-start=\"528\" data-end=\"533\">pdo</code></li><li data-start=\"538\" data-end=\"552\"><code data-start=\"540\" data-end=\"550\">fileinfo</code></li></ul></li><li data-start=\"597\" data-end=\"670\">🖥️ <strong data-start=\"603\" data-end=\"620\">نظام التشغيل:</strong> Linux (مثل Ubuntu أو CentOS) أو Windows Server.</li></ul><h3 data-start=\"672\" data-end=\"705\"><strong data-start=\"676\" data-end=\"703\"><br></strong></h3><h5><b>التوصيات لتحسين الأداء:</b></h5><ul data-start=\"706\" data-end=\"889\"><li data-start=\"706\" data-end=\"768\">🟠 تفعيل التخزين المؤقت (مثل OPcache) لتحسين سرعة التنفيذ.</li><li data-start=\"769\" data-end=\"832\">🟢 إعداد Nginx مع PHP-FPM للأداء العالي في البيئات الكبيرة.</li><li data-start=\"833\" data-end=\"889\">📂 ضبط صلاحيات الملفات والمجلدات لضمان أمان المحتوى.</li></ul><p data-start=\"891\" data-end=\"956\">بهذه المتطلبات، ستكون جاهزًا لتشغيل سكربت \"وثيقة\" بكل كفاءة 🚀</p>', 'perating-requirements', 'enable', NULL, 6, '2025-02-27 12:36:01', '2025-02-15 20:08:41');
